import React from "react";
import Card from "./Card";
import { SeriesData } from "./Series";

const favSeries = "amazon";
const FavSeries = () => {
  if (favSeries === "netflix") {
    return (
      <Card
        key={SeriesData[1].id}
        imgsrc={SeriesData[1].imgsrc}
        title={SeriesData[1].title}
        series={SeriesData[1].series}
        link={SeriesData[1].link}
      />
    );
  } else { // for amazon series
      return (
        <Card
          key={SeriesData[3].id}
          imgsrc={SeriesData[3].imgsrc}
          title={SeriesData[3].title}
          series={SeriesData[3].series}
          link={SeriesData[3].link}
        />
      );
  }
};

const App = () => {
  <>
    <h1 className="heading_style">LIST OF TOP 5 NETFLIX SERIES</h1>;
    <FavSeries />
  </>;
};

export default App;
