import React from "react";
import Card from "./Card";
import { SeriesData } from "./Series";

const favSeries = "amazon";
const Series = () => {
  if(favSeries==="netflix"){
    return (
      <>
      <Card
        key={SeriesData[1].id}
        imgsrc={SeriesData[1].imgsrc}
        title={SeriesData[1].title}
        series={SeriesData[1].series}
        link={SeriesData[1].link}
      />
      <Card
        key={SeriesData[2].id}
        imgsrc={SeriesData[2].imgsrc}
        title={SeriesData[2].title}
        series={SeriesData[2].series}
        link={SeriesData[2].link}
      />
      <Card
        key={SeriesData[4].id}
        imgsrc={SeriesData[4].imgsrc}
        title={SeriesData[4].title}
        series={SeriesData[4].series}
        link={SeriesData[4].link}
      />
      <Card
        key={SeriesData[5].id}
        imgsrc={SeriesData[5].imgsrc}
        title={SeriesData[5].title}
        series={SeriesData[5].series}
        link={SeriesData[1].link}
      />
        
        </>
      );
  }
  else{
    return (
      <Card
        key={SeriesData[3].id}
        imgsrc={SeriesData[3].imgsrc}
        title={SeriesData[3].title}
        series={SeriesData[3].series}
        link={SeriesData[3].link}
      />
    );
  }
}

const App = () => (
  <>
    <h1 className="heading_style">LIST OF TOP 5 NETFLIX SERIES</h1>;
    {SeriesData.map((v, index) => {
      console.log(index);
      return (
        <Card
          key={v.id}
          imgsrc={v.imgsrc}
          title={v.title}
          series={v.series}
          link={v.link}
        />
      );
    })}
  </>
);

export default App;
